module Ant
  class ImageComponent < ViewComponent::Base
    def initialize(src:, alt: "", width: nil, height: nil, preview: false,
                   fallback: nil, placeholder: nil, **html_options)
      @src = src
      @alt = alt
      @width = width
      @height = height
      @preview = preview
      @fallback = fallback
      @placeholder = placeholder
      @html_options = html_options
    end

    def container_classes
      base = "inline-block relative"
      classes = [ base, @html_options[:class] ]
      classes.compact.join(" ")
    end

    def image_classes
      "block w-full h-full object-cover"
    end

    def image_style
      styles = []
      styles << "width: #{@width}px" if @width
      styles << "height: #{@height}px" if @height
      styles.join("; ") unless styles.empty?
    end

    def default_placeholder
      "data:image/svg+xml,%3Csvg xmlns='http://www.w3.org/2000/svg' viewBox='0 0 400 300'%3E%3Crect width='400' height='300' fill='%23f0f0f0'/%3E%3Ctext x='50%25' y='50%25' dominant-baseline='middle' text-anchor='middle' font-family='sans-serif' font-size='18' fill='%23999'%3ELoading...%3C/text%3E%3C/svg%3E"
    end

    def default_fallback
      "data:image/png;base64,"
    end
  end
end
